mapper.select:
SELECT rl.refund_idx, rl.refund_amt, rl.refund_date, rl.why, rl.order_idx, ol.user_id FROM refund_list rl LEFT JOIN order_list ol ON rl.order_idx = ol.order_idx WHERE rl.refund_date BETWEEN #{startDate} AND #{endDate}
AND ol.user_id LIKE CONCAT('%', #{search}, '%')
ORDER BY rl.refund_amt ASC ORDER BY rl.refund_amt DESC ORDER BY rl.refund_date ASC ORDER BY rl.refund_date DESC ORDER BY rl.why ASC ORDER BY rl.why DESC ORDER BY rl.refund_date DESC
LIMIT 10 OFFSET #{offset}
